import React, { useEffect, useState } from "react";
import "./Dashboard.css";

const today = new Date();
const formattedDate = today.toLocaleDateString("en-US", {
  weekday: "long",
  year: "numeric",
  month: "long",
  day: "numeric",
});

const Dashboard = () => {
  const [quote, setQuote] = useState("Every day is a fresh start."); // Default quote
  const [author, setAuthor] = useState(""); // Default author

  useEffect(() => {
    const fetchQuote = async () => {
      try {
        // Fetch a random quote from the QuoteGarden API
        const response = await fetch(
          "https://quote-garden.onrender.com/api/v3/quotes/random"
        );

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data = await response.json();
        console.log("Fetched Data:", data); // Debugging

        if (data && data.data) {
          setQuote(data.data[0].quoteText.trim()); // Update the quote
          setAuthor(data.data[0].quoteAuthor); // Update the author
        }
      } catch (error) {
        console.error("Error fetching the quote:", error);
      }
    };

    fetchQuote();
  }, []); // Runs only once on component mount

  return (
    <div className="dashboard">
      <header className="header">
        <div className="profile">
          <span className="profile-icon">ðŸ‘¤</span>
          <span className="username">Username</span>
        </div>
        <div className="menu-icon">â˜°</div>
      </header>

      <div className="content">
        {/* Sidebar */}
        <aside className="sidebar">
          <div className="module-box">
            <h4>ðŸ“… Calendar</h4>
            <p>{formattedDate}</p>
          </div>
          <div className="module-box">
            <h4>ðŸ’¡ Quote of the Day</h4>
            <p>"{quote}"</p>
            {author && <small>- {author}</small>}
          </div>
        </aside>
      </div>
    </div>
  );
};

export default Dashboard;
