import React, { useState, useEffect } from "react";
import "./MealsDialog.css"; // Custom CSS for MealsDialog
import axios from "axios";

const MealsDialog = ({ isVisible, onClose }) => {
  const [mealDetails, setMealDetails] = useState({});
  const [showRemoveButtons, setShowRemoveButtons] = useState(false);

  useEffect(() => {
    if (isVisible) {
      fetchMealDetails();
    }
  }, [isVisible]);

  const fetchMealDetails = async () => {
    try {
      const response = await axios.get("http://localhost:5001/api/user-meals-details");
      const mealsData = response.data;

      const randomSelections = Object.entries(mealsData).reduce((result, [mealId, mealItems]) => {
        const combinedItems = [...mealItems.foods, ...mealItems.drinks];
        const randomItems = combinedItems.sort(() => 0.5 - Math.random()).slice(0, 3);
        result[mealId] = randomItems;
        return result;
      }, {});

      setMealDetails(randomSelections);
    } catch (error) {
      console.error("Error fetching meal details:", error);
    }
  };

  const removeItem = (mealId, index) => {
    setMealDetails((prevDetails) => {
      const updatedMeal = prevDetails[mealId].filter((_, idx) => idx !== index);
      return {
        ...prevDetails,
        [mealId]: updatedMeal,
      };
    });
  };

  if (!isVisible) return null;

  return (
    <div className="meals-dialog-overlay">
      <div className="meals-dialog-box">
        <div className="dialog-header">
          <h3>Meals List</h3>
          <button
            className="toggle-remove-btn"
            onClick={() => setShowRemoveButtons((prev) => !prev)}
          >
            {showRemoveButtons ? "Hide Remove" : "Show Remove"}
          </button>
        </div>

        {Object.keys(mealDetails).length > 0 ? (
          <div className="meals-container">
            {Object.entries(mealDetails).map(([mealId, items]) => (
              <div key={mealId} className="meal-section">
                <h4>Meal ID: {mealId}</h4>
                <ul className="meal-items">
                  {items.map((item, idx) => (
                    <li key={idx} className="meal-item">
                      <span>
                        {item.FoodName || item.DrinkName} - {item.CaloriesTotal} kcal ({item.Quantity} units)
                      </span>
                      {showRemoveButtons && (
                        <button
                          className="remove-btn"
                          onClick={() => removeItem(mealId, idx)}
                        >
                          -
                        </button>
                      )}
                    </li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        ) : (
          <p>No meals available</p>
        )}
        <div className="meals-dialog-buttons">
          <button className="btn" onClick={onClose}>
            Close
          </button>
        </div>
      </div>
    </div>
  );
};

export default MealsDialog;
