import React, { useEffect, useState } from 'react';

const Dashboard = () => {
  const [exercises, setExercises] = useState([]); // State to store exercises

  // Fetch exercises from the backend
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:5001/data'); // Fetch from the backend
        if (!response.ok) {
          throw new Error('Failed to fetch data');
        }
        const data = await response.json();
        // Randomly select 3 exercises from the fetched data
        const shuffled = data.sort(() => 0.5 - Math.random());
        const selected = shuffled.slice(0, 3);
        setExercises(selected);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      <h1>Random Exercises</h1>
      <div>
        {exercises.map((exercise, index) => (
          <div key={index} style={{ marginBottom: '20px' }}>
            <h3>{exercise.ExerciseName}</h3>
            <p>Reps: {exercise.Reps}</p>
            <p>Time: {exercise.Time} minutes</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
