import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Dashboard.css";
import "@fortawesome/fontawesome-free/css/all.min.css";
import "bootstrap/dist/css/bootstrap.min.css";
import Dialog from "./Dialog";
import WorkoutDialog from "./WorkoutDialog";
import SearchDialog from "./SearchDialog";
import ResultDialog from "./ResultDialog";

const today = new Date();
const formattedDate = today.toLocaleDateString("en-US", {
  weekday: "long",
  year: "numeric",
  month: "long",
  day: "numeric",
});

const Dashboard = () => {
  const [quote, setQuote] = useState(null);
  const [author, setAuthor] = useState(null);
  const [isDialogVisible, setIsDialogVisible] = useState(false);
  const [isWorkoutDialogVisible, setIsWorkoutDialogVisible] = useState(false);
  const [currentMealType, setCurrentMealType] = useState(null);
  const [searchData, setSearchData] = useState(null);
  const [exercises, setExercises] = useState([]);
  const [meals, setMeals] = useState({
    breakfast: { food: [], drink: [] },
    lunch: { food: [], drink: [] },
    dinner: { food: [], drink: [] },
  });

  const [recommendations, setRecommendations] = useState({
    breakfast: { food: [], drink: [] },
    lunch: { food: [], drink: [] },
    dinner: { food: [], drink: [] },
  });

  const showDialog = (mealType) => {
    setCurrentMealType(mealType);
    setIsDialogVisible(true);
  };

  const closeDialog = () => setIsDialogVisible(false);

  const showWorkoutDialog = () => setIsWorkoutDialogVisible(true);
  const closeWorkoutDialog = () => setIsWorkoutDialogVisible(false);

  const handleUserInput = async (data) => {
    const mealType = currentMealType;
    if (!mealType || !meals[mealType]) {
      alert("Invalid meal type.");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5001/api/add-item", { ...data, mealType });
      if (response.status === 200) {
        if (data.selection === "Food") {
          setMeals((prevMeals) => ({
            ...prevMeals,
            [mealType]: {
              ...prevMeals[mealType],
              food: [
                ...prevMeals[mealType].food,
                {
                  FoodName: data.name,
                  CaloriesTotal: data.calories,
                  Quantity: data.qty,
                },
              ],
            },
          }));
        } else if (data.selection === "Drink") {
          setMeals((prevMeals) => ({
            ...prevMeals,
            [mealType]: {
              ...prevMeals[mealType],
              drink: [
                ...prevMeals[mealType].drink,
                {
                  DrinkName: data.name,
                  CaloriesTotal: data.calories,
                  Quantity: data.qty,
                },
              ],
            },
          }));
        }
      }
    } catch (error) {
      console.error("Error adding item:", error);
      alert("Failed to add item to the server.");
    }
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const quoteResponse = await axios.get("http://localhost:5001/api/random-quote");
        setQuote(quoteResponse.data.quote);
        setAuthor(quoteResponse.data.author);

        const exercisesResponse = await axios.get("http://localhost:5001/api/exercises");
        setExercises(exercisesResponse.data);

        const breakfastResponse = await axios.get("http://localhost:5001/api/foodrink");
        const lunchResponse = await axios.get("http://localhost:5001/api/foodrink");
        const dinnerResponse = await axios.get("http://localhost:5001/api/foodrink");

        setRecommendations({
          breakfast: { food: breakfastResponse.data.food, drink: breakfastResponse.data.drink },
          lunch: { food: lunchResponse.data.food, drink: lunchResponse.data.drink },
          dinner: { food: dinnerResponse.data.food, drink: dinnerResponse.data.drink },
        });
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="dashboard">
      <header className="header">
        <div className="profile">
          <span className="profile-icon">👤</span>
          <span className="username">Username</span>
        </div>
      </header>
      <div className="content">
        <div className="meals">
          {["breakfast", "lunch", "dinner"].map((mealType) => (
            <div key={mealType} className="meal-section">
              <h5>{mealType.toUpperCase()}</h5>
              <button onClick={() => showDialog(mealType)}>Add {mealType}</button>
              <h6>User-Added Items:</h6>
              <ul>
                {meals[mealType].food.map((item, index) => (
                  <li key={index}>
                    {item.FoodName} ({item.CaloriesTotal} kcal, {item.Quantity} units)
                  </li>
                ))}
                {meals[mealType].drink.map((item, index) => (
                  <li key={index}>
                    {item.DrinkName} ({item.CaloriesTotal} kcal, {item.Quantity} units)
                  </li>
                ))}
              </ul>
              <h6>Recommended:</h6>
              <ul>
                {recommendations[mealType].food.map((item, index) => (
                  <li key={index}>
                    {item.FoodName} ({item.CaloriesTotal} kcal, {item.Quantity} units)
                  </li>
                ))}
                {recommendations[mealType].drink.map((item, index) => (
                  <li key={index}>
                    {item.DrinkName} ({item.CaloriesTotal} kcal, {item.Quantity} units)
                  </li>
                ))}
              </ul>
            </div>
          ))}
        </div>
        <div className="exercises">
          <h3>Workout of the Day</h3>
          {exercises.map((exercise, index) => (
            <div key={index}>
              <h5>{exercise.ExerciseName}</h5>
              <p>Reps: {exercise.Reps}</p>
              <p>Time: {exercise.Time}</p>
            </div>
          ))}
        </div>
      </div>
      <Dialog
        title="Add Food or Drink"
        isVisible={isDialogVisible}
        onClose={closeDialog}
        onSubmit={handleUserInput}
      />
      <WorkoutDialog
        isVisible={isWorkoutDialogVisible}
        onClose={closeWorkoutDialog}
        onSubmit={() => {}}
      />
    </div>
  );
};

export default Dashboard;
